import React, { useState } from "react";
import dooplon_optimizator from "../functions/doploon_optimizator";

import FormDoploon from "./FormDoploon";

const Content = () => {
  const [doploons, setDoploons] = useState();
  const [small, setSmall] = useState();
  const [medium, setMedium] = useState();
  const [great, setGreat] = useState();
  const [powerful, setPowerful] = useState();
  const [showResult, setShowResult] = useState(false);

  const handleSubmit = async (e) => {
    e.preventDefault();

    const result = dooplon_optimizator(
      doploons,
      small,
      medium,
      great,
      powerful
    );
  };

  return (
    <div className=' flex justify-center'>
      <div className='bg-white/80 rounded p-4 shadow '>
        {!showResult ? (
          <FormDoploon data={(doploons, small, medium, great, powerful)} />
        ) : null}
      </div>
    </div>
  );
};

export default Content;
