const knapsack = (items, capacity) => {
  const n = items.length;
  const table = Array.from({ length: n + 1 }, () =>
    Array(capacity + 1).fill(0)
  );

  for (let i = 1; i <= n; i++) {
    const { weight, value } = items[i - 1];

    for (let j = 1; j <= capacity; j++) {
      let maxVal = table[i - 1][j];
      for (let k = 1; k <= Math.floor(j / weight); k++) {
        const newVal = table[i - 1][j - weight * k] + value * k;
        if (newVal > maxVal) {
          maxVal = newVal;
        }
      }
      table[i][j] = maxVal;
    }
  }

  const result = [];
  let i = n;
  let j = capacity;
  while (i > 0 && j > 0) {
    const item = items[i - 1];
    if (!item) {
      break;
    }

    const { weight, value } = item;
    if (table[i][j] !== table[i - 1][j]) {
      const count = Math.round((table[i][j] - table[i - 1][j]) / value);
      if (count >= 0) {
        result.push({ ...item, count });
        j -= weight * count;
      }
    }
    i -= 1;
  }

  return result.reverse();
};

export default knapsack;
