import React, { useState } from "react";
import FormDoploon from "./FormDoploon";

const Content = () => {
  const [showResult, setShowResult] = useState(false);
  const [resultData, setResultData] = useState([]);
  const [totalValue, setTotalValue] = useState();

  const formatData = (data) => {
    const scrollName = Object.keys(data)[0];

    setResultData((resultData) => [
      ...resultData,
      { [scrollName]: data[scrollName], total_price: data["total_price"] },
    ]);
  };

  const handleSubmit = (result) => {
    const { total } = result[4];
    setTotalValue(total);

    result.map((data) => (data.total_price > 0 ? formatData(data) : null));

    setShowResult(true);
  };

  console.log(resultData);

  return (
    <div className=' flex justify-center'>
      <div className='bg-white/80 rounded p-4 shadow '>
        {!showResult ? <FormDoploon handleSubmit={handleSubmit} /> : null}
      </div>
    </div>
  );
};

export default Content;
