import React, { useEffect } from "react";
import { Route, Routes, useLocation } from "react-router-dom";

import Homepage from "./pages/Homepage";

import { useDispatch } from "react-redux";

const App = () => {
  let data;
  const { pathname } = useLocation();

  if (pathname.includes("pt")) {
    data = require("./translate/PT_BR.json");
  }

  // if (pathname.includes("fr")) {
  //   data = require("./translate/FR.json");
  // }

  // if (pathname.includes("es")) {
  //   data = require("./translate/EN.json");
  // }

  if (pathname.includes("en")) {
    data = require("./translate/ES.json");
  }

  console.log(data);

  useEffect(() => {
    console.log("©2023 Isaac's Magno. All rights reserved.");
  }, []);

  return (
    <Routes>
      <Route path='/pt' element={<Homepage translateData={data} />} />
      <Route path='/fr' element={<Homepage translateData={data} />} />
      <Route path='/en' element={<Homepage translateData={data} />} />
      <Route path='/es' element={<Homepage translateData={data} />} />
    </Routes>
  );
};

export default App;
