import React, { useEffect, useState } from "react";

const Modal = ({ isVisible, setVisible }) => {
  const [lang, setLang] = useState("PT");
  const [buttonDisplay, setButtonDisplay] = useState();

  useEffect(() => {
    if (lang === "PT")
      setButtonDisplay([
        <img src={require("../images/flags/portugal.png")} width={40} />,
        <p className='font-extrabold ml-2'>PT</p>,
      ]);

    if (lang === "EN")
      setButtonDisplay([
        <img src={require("../images/flags/usa.png")} width={40} />,
        <p className='font-extrabold ml-2'>EN</p>,
      ]);

    if (lang === "ES")
      setButtonDisplay([
        <img src={require("../images/flags/spain.png")} width={40} />,
        <p className='font-extrabold ml-2'>ES</p>,
      ]);

    if (lang === "FR")
      setButtonDisplay([
        <img src={require("../images/flags/france.png")} width={40} />,
        <p className='font-extrabold ml-2'>FR</p>,
      ]);
  }, [lang]);

  const HandleItemClick = (e) => {
    if (e.target && e.target.matches("li")) {
      const content = e.target.textContent;
      setLang(content);
      setVisible(false);
    }
  };

  const HandleImageClick = (e) => {
    e.stopPropagation(); // Impede que o clique se propague para o elemento 'li'
    const parentLi = e.target.closest("li");
    if (parentLi) {
      parentLi.click(); // Simula um clique no elemento 'li'
    }
  };

  return (
    <div>
      <button
        className='bg-white p-2 px-3 mr-4 flex justify-between items-center'
        onClick={() => setVisible(!isVisible)}
      >
        <span>{buttonDisplay[0]}</span>
        <span>{buttonDisplay[1]}</span>
      </button>
      {isVisible ? (
        <div className='bg-white rounded-b mr-4'>
          <ul className='' onClick={HandleItemClick}>
            <li
              className='flex justify-between items-center p-1 font-extrabold hover:opacity-70 hover:cursor-pointer'
              onClick={({ target }) => console.log(target)}
            >
              <a href='#' className='mr-2'>
                PT
              </a>
              <img
                src={require("../images/flags/portugal.png")}
                width={40}
                onClick={HandleImageClick}
              />
            </li>
            <li className='flex justify-between  items-center p-1 font-extrabold hover:opacity-70 hover:cursor-pointer'>
              <a href='#' className='mr-2'>
                EN
              </a>
              <img
                src={require("../images/flags/usa.png")}
                width={40}
                onClick={HandleImageClick}
              />
            </li>
            <li className='flex justify-between  items-center p-1 font-extrabold hover:opacity-70 hover:cursor-pointer'>
              <a href='#' className='mr-2'>
                ES
              </a>
              <img
                src={require("../images/flags/spain.png")}
                width={40}
                onClick={HandleImageClick}
              />
            </li>
            <li className='flex justify-between  items-center p-1 font-extrabold hover:opacity-70 hover:cursor-pointer'>
              <a href='#' className='mr-2'>
                FR
              </a>
              <img
                src={require("../images/flags/france.png")}
                width={40}
                onClick={HandleImageClick}
              />
            </li>
          </ul>
        </div>
      ) : null}
    </div>
  );
};

export default Modal;
