const knapsack = (items, capacity) => {
  const n = items.length;
  const table = Array.from({ length: n + 1 }, () =>
    Array(capacity + 1).fill(0)
  );

  for (let i = 1; i <= n; i++) {
    const { weight, value, count = 1 } = items[i - 1];
    for (let j = 1; j <= capacity; j++) {
      let maxVal = table[i - 1][j];
      for (let k = 1; k <= count; k++) {
        if (weight <= j && table[i - 1][j - weight * k] + value * k > maxVal) {
          maxVal = table[i - 1][j - weight * k] + value * k;
        }
      }
      table[i][j] = maxVal;
    }
  }

  const result = [];
  let i = n;
  let j = capacity;
  while (i > 0 && j > 0) {
    const { weight, value, count = 1 } = items[i - 1];
    if (table[i][j] !== table[i - 1][j]) {
      result.push(items[i - 1]);
      j -= weight;
      items[i - 1].count = items[i - 1].count - 1;
    } else if (table[i][j] === table[i - 1][j] && count > 1) {
      items[i - 1].count = items[i - 1].count - 1;
    }
    i -= 1;
  }

  console.log(result);

  return result;
};
