import React from "react";
import imagem from "../images/flags/portugal.png";

const Header = () => {
  const opcao1Style = {
    backgroundImage: `url(${imagem})`,
    backgroundSize: "contain",
    backgroundPosition: "center",
    backgroundRepeat: "no-repeat",
  };

  return (
    <div className='flex justify-between bg-red-500'>
      <div className='flex-row p-10 pb-16 bg-yellow-500'>
        <h1 className=' ml-10 text-7xl font-extrabold opacity-30  '>
          Doploons
        </h1>
        <h1 className='ml-24 text-7xl font-extrabold opacity-30 '>
          Calculator
        </h1>
      </div>
      <div className='justify-end bg-blue-500'>
        <select>
          <option style={opcao1Style}>PT</option>
          <option>EN</option>
          <option>FR</option>
          <option>ES</option>
        </select>
      </div>
    </div>
  );
};

export default Header;
