import React, { useState } from "react";
import { doploon_optimization } from "../services/axiosRequests";

const FormDoploon = ({ handleSubmit }) => {
  const [doploons, setDoploons] = useState();
  const [small, setSmall] = useState();
  const [medium, setMedium] = useState();
  const [great, setGreat] = useState();
  const [powerful, setPowerful] = useState();

  const handleCalculate = async (e) => {
    e.preventDefault();
    const result = await doploon_optimization({
      "doploons": doploons,
      "small": small,
      "medium": medium,
      "great": great,
      "powerful": powerful,
    });

    console.log(result);

    handleSubmit();
  };

  return (
    <form className='flex gap-2 p-3' style={{ flexDirection: "column" }}>
      <div className='flex justify-between items-center font-bold'>
        <span className='font-extrabold text-xl opacity-60'>Doploons:</span>
        <input
          type='number'
          className='ml-10 text-gray-700 font-bold  border-gray-300 rounded-md py-2 text-center sm:text-sm placeholder-gray-400 focus:outline-none focus:ring-1 focus:ring-gray-500 focus:border-gray-500 bg-transparent'
          placeholder='Quantidade'
          value={doploons}
          onChange={({ target: { value } }) => setDoploons(value)}
        />
      </div>
      <div className='flex justify-between items-center font-bold'>
        <span className='font-extrabold text-xl opacity-60'>Pequeno:</span>
        <input
          type='number'
          className='ml-10 text-gray-700 font-bold  border-gray-300 rounded-md py-2 text-center sm:text-sm placeholder-gray-400 focus:outline-none focus:ring-1 focus:ring-gray-500 focus:border-gray-500 bg-transparent'
          placeholder='PreÃ§o'
          value={small}
          onChange={({ target: { value } }) => setSmall(value)}
        />
      </div>
      <div className='flex justify-between items-center font-bold'>
        <span className='font-extrabold text-xl opacity-60'>MÃ©dio:</span>
        <input
          type='number'
          className='ml-10 text-gray-700 font-bold  border-gray-300 rounded-md py-2 text-center sm:text-sm placeholder-gray-400 focus:outline-none focus:ring-1 focus:ring-gray-500 focus:border-gray-500 bg-transparent'
          placeholder='PreÃ§o'
          value={medium}
          onChange={({ target: { value } }) => setMedium(value)}
        />
      </div>
      <div className='flex justify-between items-center font-bold'>
        <span className='font-extrabold text-xl opacity-60'>Grande:</span>
        <input
          type='number'
          className='ml-10 text-gray-700 font-bold  border-gray-300 rounded-md py-2 text-center sm:text-sm placeholder-gray-400 focus:outline-none focus:ring-1 focus:ring-gray-500 focus:border-gray-500 bg-transparent'
          placeholder='PreÃ§o'
          value={great}
          onChange={({ target: { value } }) => setGreat(value)}
        />
      </div>
      <div className='flex justify-between items-center font-bold'>
        <span className='font-extrabold text-xl opacity-60'>Poderoso:</span>
        <input
          type='number'
          className='ml-10 text-gray-700 font-bold  border-gray-300 rounded-md py-2 text-center sm:text-sm placeholder-gray-400 focus:outline-none focus:ring-1 focus:ring-gray-500 focus:border-gray-500 bg-transparent'
          placeholder='PreÃ§o'
          value={powerful}
          onChange={({ target: { value } }) => setPowerful(value)}
        />
      </div>
      <button
        className='bg-green-700 rounded hover:bg-green-600 p-2 font-extrabold text-white mt-4'
        onClick={(e) => handleCalculate(e)}
      >
        Calcular
      </button>
    </form>
  );
};

export default FormDoploon;
