const knapsack = (items, capacity) => {
  const n = items.length;
  const table = Array.from({ length: n + 1 }, () =>
    Array(capacity + 1).fill(0)
  );

  for (let i = 1; i <= n; i++) {
    const { weight, value } = items[i - 1];

    for (let j = 1; j <= capacity; j++) {
      let maxVal = table[i - 1][j];
      for (let k = 0; k * weight <= j; k++) {
        const newVal = table[i - 1][j - k * weight] + k * value;
        if (newVal > maxVal) {
          maxVal = newVal;
        }
      }
      table[i][j] = maxVal;
    }
  }

  const result = [];
  let i = n;
  let j = capacity;
  while (i > 0 && j > 0) {
    const { weight, value } = items[i - 1];
    let k = 0;
    while (
      k * weight <= j &&
      table[i][j] === table[i - 1][j - k * weight] + k * value
    ) {
      k++;
    }
    if (k > 0) {
      result.push({ ...items[i - 1], count: k });
      j -= k * weight;
    }
    i--;
  }

  return result.reverse();
};

export default knapsack;
