import React, { useState } from "react";
import FormDoploon from "./FormDoploon";

const Content = () => {
  const [showResult, setShowResult] = useState(false);
  const [resultData, setResultData] = useState([]);
  const [totalValue, setTotalValue] = useState();
  const [scrollType, setScrollType] = useState();

  const formatData = (data) => {
    const scrollName = Object.keys(data)[0];

    setResultData((resultData) => [
      ...resultData,
      {
        scroll: scrollName,
        quantity: data[scrollName],
        total_price: data["total_price"],
      },
    ]);
  };

  const handleSubmit = (result, selectedScroll) => {
    const { total } = result[4];
    console.log(selectedScroll);
    setScrollType(selectedScroll);
    setTotalValue(total);

    result.map((data) => (data.total_price > 0 ? formatData(data) : null));

    setShowResult(true);
  };

  const handleButtonBack = () => {
    setResultData([]);
    setTotalValue(0);
    setScrollType("");
    setShowResult(false);
  };

  return (
    <div className=' flex justify-center'>
      <div className='bg-white/80 rounded p-4 shadow '>
        {!showResult ? (
          <FormDoploon handleSubmit={handleSubmit} />
        ) : (
          <div className='flex'>
            <div className='flex'>
              {resultData.map((scroll) => (
                <div className=' bg-red-500'>
                  <img
                    src={require(`../images/scrolls/${scrollType}_${scroll.scroll}.png`)}
                    className='text-center'
                  />
                  <p className='text-center'>Tipo: {scroll.scroll}</p>
                  <p className='text-center'>Quantidade: {scroll.quantity}</p>
                  <p className='text-center'>
                    Total: {scroll.total_price.toLocaleString("pt-BR")}
                  </p>
                </div>
              ))}
            </div>
            <div className='flex justify-center p-3 bg-blue-500 items-center gap-1'>
              <img src={require("../images/kamas.png")} width={50} />
              <p className='font-extrabold'>
                {totalValue.toLocaleString("pt-BR")}K
              </p>
            </div>
            <button
              className='bg-green-700 p-3'
              onClick={() => handleButtonBack()}
            >
              Voltar
            </button>
          </div>
        )}
      </div>
    </div>
  );
};

export default Content;
