import React, { useState, useEffect } from "react";
import { useLocation } from "react-router-dom";

import FormDoploon from "./FormDoploon";

const Content = ({ buttons, placeholders, results, scrolls, sizes }) => {
  const [showResult, setShowResult] = useState(false);
  const [resultData, setResultData] = useState([]);
  const [totalValue, setTotalValue] = useState();
  const [scrollType, setScrollType] = useState();
  const [scrollName, setScrollName] = useState([]);
  const [currentScrolls, setCurrentScrolls] = useState([]);

  const { pathname } = useLocation();

  const handleButtonBack = () => {
    setResultData([]);
    setTotalValue(0);
    setScrollType("");
    setShowResult(false);
  };

  const handleSubmit = (result, selectedScroll) => {
    const { total } = result[4];
    setScrollType(selectedScroll);
    setTotalValue(total);

    const newCurrentScrolls = [];
    const newResultData = [];

    result.forEach((data) => {
      if (data.total_price > 0) {
        const scrollName = Object.keys(data)[0];
        newCurrentScrolls.push(scrollName);

        let translatedName;
        if (scrollName === "small") translatedName = sizes[0];
        if (scrollName === "medium") translatedName = sizes[1];
        if (scrollName === "great") translatedName = sizes[2];
        if (scrollName === "powerful") translatedName = sizes[3];

        newResultData.push({
          scroll: translatedName,
          quantity: data[scrollName],
          total_price: data["total_price"],
        });
      }
    });

    setCurrentScrolls(newCurrentScrolls);
    setResultData(newResultData);
    setShowResult(true);
  };

  // console.log("useEffect", resultData);

  return (
    <div className='flex justify-center'>
      <div className='bg-white/80 rounded p-4 shadow '>
        {!showResult ? (
          <FormDoploon
            handleSubmit={handleSubmit}
            scrolls={scrolls}
            placeholders={placeholders}
            sizes={sizes}
            buttons={buttons[0]}
          />
        ) : (
          <div className=''>
            <div className='flex gap-1'>
              {resultData
                ? resultData.map((scroll) => (
                    <div className='border-2 px-3 border-gray-700/20 rounded'>
                      <img
                        src={require(`../images/scrolls/${scrollType}_${scrollName}.png`)}
                        className='text-center'
                        width={130}
                      />
                      {/* <span className='flex justify-between'>
                        <p className='font-extrabold'>{results[0]}</p>
                        <p className='font-bold'>{scroll.scroll}</p>
                      </span>
                      <span className='flex justify-between'>
                        <p className='font-extrabold'>{results[1]}</p>
                        <p className='font-bold'>{scroll.quantity}</p>
                      </span>
                      <span className='flex justify-between'>
                        <p className='font-extrabold'>{results[2]}</p>
                        <p className='font-bold'>
                          {scroll.total_price.toLocaleString("pt-BR")}K
                        </p>
                      </span> */}
                    </div>
                  ))
                : null}
            </div>
            <div className='flex justify-center p-3 items-center gap-1'>
              <img src={require("../images/kamas.png")} width={50} />
              <p className='font-extrabold'>
                {totalValue.toLocaleString("pt-BR")}K
              </p>
            </div>
            <div className='flex justify-center'>
              <button
                className='bg-blue-700 py-2 mx-3.5 rounded white hover:bg-blue-600 font-extrabold grow'
                onClick={() => handleButtonBack()}
              >
                {buttons[1]}
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Content;
